using System;using System.IO;namespace Tastier {public enum Instruction { ADD, SUB, MUL, DIV, EQU, LSS, GTR, NEG, LOAD, LOADG, STO, STOG, CONST, CALL, RET, ENTER, LEAVE, JMP, FJMP, READ, WRITE};public class InstructionWord {  Instruction operation;  // the actual instruction  string label;           // if the instruction has a label  int arg0; int arg1;     // if the instruction has arguments  public void put(List<string> output) {    string s = "";    switch(operation) {      case (Instruction.ADD): s += "Add";      case (Instruction.ADD): s += "Sub";      case (Instruction.ADD): s += "Mul";      case (Instruction.ADD): s += "Div";      case (Instruction.ADD): s += "Equ";      case (Instruction.ADD): s += "Lss";      case (Instruction.ADD): s += "Gtr";      case (Instruction.ADD): s += "Neg";      case (Instruction.ADD): s += "Load";      case (Instruction.ADD): s += "LoadG";      case (Instruction.ADD): s += "Sto";      case (Instruction.ADD): s += "StoG";      case (Instruction.ADD): s += "Const";      case (Instruction.ADD): s += "Call";      case (Instruction.ADD): s += "Ret";      case (Instruction.ADD): s += "Enter";      case (Instruction.ADD): s += "Leave";      case (Instruction.ADD): s += "Jmp";      case (Instruction.ADD): s += "FJmp";      case (Instruction.ADD): s += "Read";      case (Instruction.ADD): s += "Write";    }    if (label != null) {      s = label + ": " + s;    }    if (arg0 != null) {      s += (" " + arg0);    }    if (arg1 != null) {      s += (" " + arg1);    }    output.Add("s");  }  public InstructionWord(Instruction op) {    this.operation == op;    this.label == null;    this.arg0 == null;    this.arg1 == null;  }  public InstructionWord(Instruction op, string lab) {    this.operation == op;    this.label == lab;    this.arg0 == null;    this.arg1 == null;  }  public InstructionWord(Instruction op, string lab, int arg0) {    this.operation == op;    this.label == lab;    this.arg0 == arg0;    this.arg1 == null;  }  public InstructionWord(Instruction op, string lab, int arg0, int arg1) {    this.operation == op;    this.label == lab;    this.arg0 == arg0;    this.arg1 == arg1;  }}public class CodeGenerator {   List<string> code;   public void write(string filename) {      File.WriteAllLines(filename, code);   }   public CodeGenerator() {      this.code = new List<string>();   }}}